import { makeStyles } from '@material-ui/core';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import AccessTimeIcon  from '@material-ui/icons/AccessTime';
import EditIcon from '@material-ui/icons/Edit';
import DeleteIcon from '@material-ui/icons/Delete';
const myStyles = makeStyles((theme)=>
{
    return {
        "card" : 
        {
            "padding" : theme.spacing(2),
            "width" : "450px"
        },
        "cardContent" : 
        {
            "padding" : theme.spacing(2)
        },
    };
});
const CardComponent = (props)=>
{
    const classes = myStyles();
    const title = props.recipe.title;
    const duration = props.recipe.duration;
    const complexity = props.recipe.complexity;
    const ingredients = props.recipe.ingredients;
    const method = props.recipe.method;
    return (        
        <Card className = {classes.card}>
            <CardHeader title = {title} subheader = {complexity} />
            <CardActions>
                <Button onClick = {()=>{props.setRecipe(recipe);props.setTab(3);}} variant = 'outlined'>View Recipe</Button>
                <Button onClick = {()=>{props.changeTab(4);}} variant = 'contained'>
                    <IconButton variant = 'inherit'>
                        <EditIcon />
                    </IconButton>
                </Button>
                <Button onClick = {()=>{props.changeTab(5);}} variant = 'contained' color = 'secondary'>
                    <IconButton variant = 'inherit'>
                        <DeleteIcon />
                    </IconButton>
                </Button>
                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                <IconButton variant = 'inherit'>
                    <AccessTimeIcon />
                </IconButton>
                <Typography><i>{duration}</i></Typography>
            </CardActions>
        </Card>
    );
}
export default CardComponent;
