import React from 'react';
import { Typography, makeStyles } from '@material-ui/core';
import AppBarComponent from './AppBarComponent';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
const myStyles = makeStyles((theme)=>
{
    return {
        "mainContainer" : 
        {
            "margin" : theme.spacing(5),
        },
        "appBarSpacer" : theme.mixins.toolbar,
        "content" : 
        {
            "marginTop" : theme.spacing(15),
            "marginLeft" : theme.spacing(15),
            "color" : "#2929aa"
        },
        "formPaper" : 
        {
            "padding" : theme.spacing(5)
        }
    };
});
const AboutUsComponent = (props)=>
{
    const classes = myStyles();
    return (
        <div className = {classes.mainContainer}>
            <AppBarComponent tabIndex = {props.tabIndex} changeTab = {props.changeTab} />
            <div className = {classes.content}>
                <Grid container spacing = {12}>
                    <Paper className = {classes.formPaper}>
                        <Typography variant = 'h2'>Welcome to Journalism By Arun!</Typography>
                        <Typography>
                            At Journalism By Arun, we're passionate about empowering individuals through education. Founded by Arun, a seasoned journalist with a deep commitment to knowledge sharing, our platform is dedicated to providing high-quality courses that inspire, educate, and transform lives.
                            With years of experience in the field of journalism, Arun understands the power of information and the importance of staying ahead in a rapidly evolving world. Recognizing the need for accessible and practical learning opportunities, he embarked on a mission to create a platform where anyone can enhance their skills, pursue their passions, and achieve their goals.
                            At the core of Journalism By Arun is a team of experts dedicated to curating and developing courses that meet the highest standards of excellence. Whether you're looking to sharpen your writing skills, dive into investigative reporting, or explore the latest trends in digital media, our diverse range of courses has something for everyone.
                            What sets us apart is our commitment to providing not just knowledge, but also practical insights and real-world applications. We believe in learning by doing, which is why our courses are designed to be interactive, engaging, and hands-on. Whether you're a beginner or a seasoned professional, you'll find valuable resources and support to help you succeed.
                            Join us on this journey of learning and discovery. Together, let's unlock your full potential and shape the future of journalism and beyond.
                        </Typography>
                        <Typography variant = 'h2'>
                            Thank you for choosing Journalism By Arun!
                        </Typography>
                    </Paper>
                </Grid>
            </div>
        </div>
    );
}
export default AboutUsComponent;
