import React from 'react';
import { Typography, makeStyles } from '@material-ui/core';
import AppBarComponent from './AppBarComponent';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
const myStyles = makeStyles((theme)=>
{
    return {
        "mainContainer" : 
        {
            "margin" : theme.spacing(5),
        },
        "appBarSpacer" : theme.mixins.toolbar,
        "content" : 
        {
            "marginTop" : theme.spacing(30),
            "marginLeft" : theme.spacing(15),
            "color" : "#2929aa"
        },
        "formPaper" : 
        {
            "padding" : theme.spacing(5)
        }
    };
});
const ContactUsComponent = (props)=>
{
    const classes = myStyles();
    return (
        <div className = {classes.mainContainer}>
            <AppBarComponent tabIndex = {props.tabIndex} changeTab = {props.changeTab} />
            <div className = {classes.content}>
                <Grid container spacing = {9}>
                    <Grid item>
                        <Typography><h4>Contact Us</h4></Typography>
                        <Typography><h5>Need Some Support Fill Out the form so that we can contact you</h5></Typography>
                        <Typography><h5>Feel Free To Ask</h5></Typography>
                    </Grid>
                    <Grid item>
                        <Paper className = {classes.formPaper}>
                            <Grid container spacing = {4}>
                                <Grid item>
                                    <TextField id = "firstName" label = "First Name" helperText = "The name by which you would like to be called" />
                                </Grid>
                                <Grid item>
                                    <TextField id = "lastName" label = "Last Name" helperText = "Your Surname here" />
                                </Grid>
                            </Grid>
                            <Grid container>
                                <Grid item>
                                    <TextField id = "email" label = "Email Id" helperText = "This will be used to contact you" />
                                </Grid>
                            </Grid>
                            <Grid container>
                            <Grid item>
                                    <TextField id = "problem" label = "Problem" helperText = "Your Problem" />
                                </Grid>
                            </Grid>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        </div>
    );
}
export default ContactUsComponent;
