import React from 'react';
import { makeStyles } from '@material-ui/core';
import AppBarComponent from './AppBarComponent';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardActions from '@material-ui/core/CardActions';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import AccessTimeIcon  from '@material-ui/icons/AccessTime';
const myStyles = makeStyles((theme)=>
{
    return {
        "mainContainer" : 
        {
            "margin" : theme.spacing(2)
        },
        "appBarSpacer" : theme.mixins.toolbar,
        "content" : 
        {
            "color" : "#2929aa"
        },
        "headerContainer" : 
        {
            "backgroundColor" : "#2929aa",
            "padding" : theme.spacing(5),
            "color" : "white"
        },
        "courses" : 
        {
            "marginTop" : theme.spacing(5) 
        },
        "card" : 
        {
            "padding" : theme.spacing(2),
            "width" : "400px"
        },
        "cardContent" : 
        {
            "padding" : theme.spacing(2)
        },
        "cardMedia" :
        {
            "padding" : theme.spacing(2),
            "width" : "400px",
            "height" : "200px"
        }
        
    };
});
const HomeComponent = (props)=>
{
    const classes = myStyles();
    return (
        <div className = {classes.mainContainer}>
            <AppBarComponent tabIndex = {props.tabIndex} changeTab = {props.changeTab} />
            <div className = {classes.content}>
                <Grid contaienr spacing = {12}>
                    <Grid item>
                        <Paper className = {classes.headerContainer}>
                            <Typography><h1>Welcome to Arun's Journalism (Journalism By Arun)</h1></Typography>
                            <Typography>
                                Are you passionate about uncovering the truth, telling compelling stories, and 
                                making a difference through the power of words? Welcome to our online platform dedicated
                                to nurturing the next generation of journalists. Whether you're a budding writer, an aspiring
                                investigative reporter, or a seasoned professional looking to refine your skills, we have a
                                range of courses tailored to meet your needs and propel your career forward.
                            </Typography>
                        </Paper>
                    </Grid>
                    <Grid item className = {classes.courses}>
                        <Paper>
                            Course We Offer
                            <Card className = {classes.card}>
                                <CardHeader title = 'Introduction to Journalism' subheader = 'Beginner' />
                            <CardMedia className = {classes.cardMedia} image = {'images/introductionToJournalism.jpeg'} />
                            <CardActions>
                                <Button onClick = {()=>{props.changeTab(3);}} variant = 'outlined'>View Details</Button>
                                <Button onClick = {()=>{props.changeTab(4);}} variant = 'contained'>Apply Now</Button>
                                &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
                                <IconButton variant = 'inherit'>
                                    <AccessTimeIcon />
                                </IconButton>
                                <Typography><i>6 Weeks</i></Typography>
                            </CardActions>
                        </Card>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        </div>
    );
}
export default HomeComponent;
