import React from 'react';
import { BrowserRouter,Routes,Route,Link } from 'react-router-dom';
import HomeComponent from './HomeComponent';
import ContactUsComponent from './ContactUsComponent';
import AboutUsComponent from './AboutUsComponent';
const App = ()=>
{
    const [tabIndex,setTabIndex] = React.useState(0);
    const changeTab = (val)=>
    {
        setTabIndex(val);
        if(val === 0)
            setComponentToShow(<HomeComponent tabIndex = {0} changeTab = {changeTab} />);
        else if (val === 1)
            setComponentToShow(<ContactUsComponent tabIndex = {1} changeTab = {changeTab} />);
        else if(val === 2)
            setComponentToShow(<AboutUsComponent tabIndex = {1} changeTab = {changeTab} />);
    }
    const [componentToShow,setComponentToShow] = React.useState(<HomeComponent tabIndex = {tabIndex} changeTab = {changeTab} />)

    return (
        <BrowserRouter>
            <Routes>
                <Route exact path = '/' element = {componentToShow}></Route>
            </Routes>
        </BrowserRouter>
    );
}
export default App;
