import React from 'react';
import { Typography, makeStyles } from '@material-ui/core';
import AppBarComponent from './AppBarComponent';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
const myStyles = makeStyles((theme)=>
{
    return {
        "mainContainer" : 
        {
            "margin" : theme.spacing(5),
        },
        "appBarSpacer" : theme.mixins.toolbar,
        "content" : 
        {
            "marginTop" : theme.spacing(10),
            "marginLeft" : theme.spacing(40),
            "color" : "#2929aa"
        },
        "formPaper" : 
        {
            "padding" : theme.spacing(5)
        }
    };
});
const ApplyComponent = (props)=>
{
    const classes = myStyles();
    return (
        <div className = {classes.mainContainer}>
            <AppBarComponent tabIndex = {props.tabIndex} changeTab = {props.changeTab} />
            <div className = {classes.content}>
                <Grid container spacing = {12}>
                    <Grid item>
                        <Paper className = {classes.formPaper}>
                            <Typography varaint = 'h2'>Apply For the Course</Typography>
                            <Typography variant = 'h2'>Introduction to Journalism</Typography>
                            <Grid container spacing = {5}>
                                <Grid item>
                                    <TextField id = "firstName" label = "First Name" helperText = "The name by which you would like to be called" />
                                </Grid>
                                <Grid item>
                                    <TextField id = "lastName" label = "Last Name" helperText = "Your Surname here" />
                                </Grid>
                            </Grid>
                            <Grid container spacing = {5}>
                                <Grid item>
                                    <TextField id = "email" label = "Email Id" helperText = "Enter Your Email Id By which we can contact you" />
                                </Grid>
                                <Grid item>
                                    <TextField id = "phone" label = "Phone Number" helperText = "Enter Your Phone Number By which we can contact you" />
                                </Grid>
                            </Grid>
                            <Grid container>
                                <Grid item>
                                    <Button variant = 'contained'>Apply Now</Button>
                                </Grid>
                            </Grid>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        </div>
    );
}
export default ApplyComponent;
