import React from 'react';
import { BrowserRouter,Routes,Route,Link } from 'react-router-dom';
import HomeComponent from './HomeComponent';
import ContactDeveloperComponent from './ContactDeveloperComponent';
import AddRecipeComponent from './AddRecipeComponent';
import ViewRecipeComponent from './ViewRecipeComponent';
const App = ()=>
{
    const [tabIndex,setTabIndex] = React.useState(0);
    const [recipe,setRecipe] = React.useState({});
    const changeTab = (val)=>
    {
        setTabIndex(val);
        if(val === 0)
            setComponentToShow(<HomeComponent tabIndex = {0} changeTab = {changeTab} setRecipe = {setRecipe} />);
        else if (val === 2)
            setComponentToShow(<ContactDeveloperComponent tabIndex = {2} changeTab = {changeTab} />);
        else if(val === 1)
            setComponentToShow(<AddRecipeComponent tabIndex = {1} changeTab = {changeTab} />);
        else if (val === 3)
            setComponentToShow(<ViewRecipeComponent tabIndex = {3} changeTab = {changeTab} recipe = {recipe} />);
    }
    const [componentToShow,setComponentToShow] = React.useState(<HomeComponent tabIndex = {tabIndex} changeTab = {changeTab} />)

    return (
        <BrowserRouter>
            <Routes>
                <Route exact path = '/' element = {componentToShow}></Route>
            </Routes>
        </BrowserRouter>
    );
}
export default App;
