import React from 'react';
import { useState } from 'react';
import { BrowserRouter,Routes,Route,Link } from 'react-router-dom';
import HomeComponent from './HomeComponent';
import ContactDeveloperComponent from './ContactDeveloperComponent';
import AddRecipeComponent from './AddRecipeComponent';
import ViewRecipeComponent from './ViewRecipeComponent';
const App = ()=>
{
    const viewRecipe = (recipe)=>
    {
        setRecipe(recipe);
        changeTab(3);
    }        
    const [tabIndex,setTabIndex] = React.useState(0);
    const [recipe,setRecipe] = React.useState({});
    console.log(JSON.stringify(recipe)+" "+tabIndex);
    const changeTab = (val)=>
    {
        setTabIndex(val);
        if(val === 0)
            setComponentToShow(<HomeComponent tabIndex = {0} changeTab = {changeTab} viewRecipe = {viewRecipe} />);
        else if (val === 2)
            setComponentToShow(<ContactDeveloperComponent tabIndex = {2} changeTab = {changeTab} />);
        else if(val === 1)
            setComponentToShow(<AddRecipeComponent tabIndex = {1} changeTab = {changeTab} />);
        else if (val === 3)
            setComponentToShow(<ViewRecipeComponent changeTab = {changeTab} recipe = {recipe} />);
    }
    const [componentToShow,setComponentToShow] = React.useState(<HomeComponent tabIndex = {tabIndex} changeTab = {changeTab} viewRecipe = {viewRecipe} />)
    return (

        <BrowserRouter>
            <Routes>
                <Route exact path = '/' element = {componentToShow}></Route>
            </Routes>
        </BrowserRouter>
    );
}
export default App;
