import React from 'react';
import { TextareaAutosize, Typography, makeStyles } from '@material-ui/core';
import AppBarComponent from './AppBarComponent';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core';
import Option from '@material-ui/core';
import Button from '@material-ui/core/Button';
const myStyles = makeStyles((theme)=>
{
    return {
        "mainContainer" : 
        {
            "margin" : theme.spacing(5),
        },
        "appBarSpacer" : theme.mixins.toolbar,
        "content" : 
        {
            "marginTop" : theme.spacing(10),
            "marginLeft" : theme.spacing(40),
            "color" : "#2929aa"
        },
        "formPaper" : 
        {
            "padding" : theme.spacing(5)
        }
    };
});
const AddRecipeComponent = (props)=>
{
    const classes = myStyles();
    return (
        <div className = {classes.mainContainer}>
            <AppBarComponent tabIndex = {props.tabIndex} changeTab = {props.changeTab} />
            <div className = {classes.content}>
                <Grid container spacing = {12}>
                    <Grid item>
                        <Paper className = {classes.formPaper}>
                            <Typography><b>Add Your Recipe</b></Typography>
                            <Grid container spacing = {12}>
                                <Grid item>
                                    <TextField id = "title" label = "Title" helperText = "Title of the Recipe" />
                                </Grid>
                            </Grid>
                            <Grid container spacing = {5}>
                                <Grid item>
                                    <TextField selectProps = {{
                                        "native" : true
                                    }} id = "complexity" select helperText = "Select How complex is Dish to prepare" label = "Complexity">
                                        <option id = "1" key = "1" value = "Easy">Easy</option>
                                        <option id = "2" key = "2" value = "Medium">Medium</option>
                                        <option id = "3" key = "3" value = "Hard">Hard</option>
                                    </TextField>
                                </Grid>
                                <Grid item>
                                    <TextField id = "duration" label = "Duration" helperText = "Time for Preparation" />
                                </Grid>
                            </Grid>
                            <Grid container spacing = {12}>
                                <Grid item>
                                    <TextField id = "ingredients" label = "Ingredients" helperText = "The items required for this recipe" multiline rows = {5} />
                                </Grid>
                            </Grid>
                            <Grid container spacing = {12}>
                                <Grid item>
                                    <TextField id = "method" label = "Method" helperText = "The steps for preparation of this recipe" multiline rows = {5} />
                                </Grid>
                            </Grid>
                            <Grid container>
                                <Grid item>
                                    <Button variant = 'contained'>Apply Now</Button>
                                </Grid>
                            </Grid>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        </div>
    );
}
export default AddRecipeComponent;
