import React from 'react';
import { Typography, makeStyles } from '@material-ui/core';
import AppBarComponent from './AppBarComponent';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
const myStyles = makeStyles((theme)=>
{
    return {
        "mainContainer" : 
        {
            "margin" : theme.spacing(5),
        },
        "appBarSpacer" : theme.mixins.toolbar,
        "content" : 
        {
            "marginTop" : theme.spacing(15),
            "marginLeft" : theme.spacing(15),
            "color" : "#2929aa"
        },
        "formPaper" : 
        {
            "padding" : theme.spacing(5)
        }
    };
});
const ViewDetailsComponent = (props)=>
{
    const classes = myStyles();
    return (
        <div className = {classes.mainContainer}>
            <AppBarComponent tabIndex = {props.tabIndex} changeTab = {props.changeTab} />
            <div className = {classes.content}>
                <Grid container spacing = {12}>
                    <Grid item>
                        <Paper className = {classes.formPaper}>
                            <Typography variant = 'h2'>
                                Introduction to Journalism
                            </Typography>
                            <Typography><h4>Course Details : </h4></Typography>
                            <Typography>
                                Welcome to "Introduction to Journalism" -  your gateway to the exciting and dynamic world of journalism. Led by seasoned journalist Arun, this course is designed to provide you with a comprehensive understanding of the fundamental principles, practices, and ethics that underpin the field of journalism.
                                Throughout this course, you will embark on a journey that covers the essential aspects of journalism, from its historical roots to its modern-day applications in the digital age. Whether you're an aspiring journalist, a curious newcomer, or simply someone interested in understanding the media landscape, this course will equip you with the knowledge and skills needed to navigate the world of journalism with confidence and proficiency.
                            </Typography>
                            <Typography><h4>Course Outline : </h4></Typography>
                            <Typography>
                                Understanding Journalism: Explore the history, evolution, and significance of journalism in society. Learn about the role of journalists as watchdogs, storytellers, and advocates for truth and transparency.

                                The Basics of Reporting: Dive into the fundamentals of reporting, including news gathering, interviewing techniques, and the principles of objectivity and accuracy.

                                News Writing Essentials: Master the art of crafting compelling and informative news stories. Learn about the structure of news articles, writing styles, and best practices for engaging readers.

                                Ethics and Standards: Examine the ethical considerations and professional standards that govern journalism. Explore topics such as objectivity, fairness, accountability, and the importance of fact-checking and verification.

                                Digital Journalism: Navigate the digital landscape and explore the impact of technology on journalism. Learn about multimedia storytelling, social media strategies, and the challenges and opportunities presented by online journalism platforms.

                                The Future of Journalism: Reflect on the future trends and innovations shaping the field of journalism. Explore emerging technologies, new media models, and the evolving role of journalists in an increasingly interconnected world.
                            </Typography>
                            <Typography><h4>
                                Course Format :
                            </h4></Typography>
                            <Typography>

                                Video lectures presented by Arun, supplemented with interactive quizzes, assignments, and real-world case studies.
                                Access to downloadable resources, reading materials, and additional learning materials to enhance your understanding of the course topics.
                                Discussion forums and community support to connect with fellow learners, share insights, and engage in meaningful discussions.
                                By the end of this course, you will emerge with a solid foundation in journalism, equipped with the skills, knowledge, and confidence to embark on your own journalistic journey. Whether you aspire to become a professional journalist, contribute to your community through citizen journalism, or simply gain a deeper appreciation for the media, "Introduction to Journalism" will empower you to make informed decisions and communicate effectively in today's media landscape.
                            </Typography>
                            <Typography><h3>
                            Enroll now and embark on an enriching learning experience that will open doors to new opportunities and possibilities in the world of journalism.
                            </h3></Typography>
                            <Grid item>
                                <Button onClick = {()=>{props.changeTab(4);}} variant = 'contained'>Apply Now</Button>
                            </Grid>
                        </Paper>
                    </Grid>
                </Grid>
            </div>
        </div>
    );
}
export default ViewDetailsComponent;
